sections.css:
.sidebar {
  background-color: @white;
  height: 100vh;
  position: fixed;
  z-index: 100;

  .logo {
    background: url(img/logo.png) no-repeat;
    height: 3 * @lh;
  }

  .logo-wrapper {
    padding-top: @lh;
    padding-bottom: @lh;
    padding-left: @gap-h;
  }

  &.sidebar-inverted {
    background-color: @gallery;
  }
}

.toolbar {
  padding-top: 4 * @lh;
}


// Orange Header
.header {
  background-color: @tangerine;
  height: 5 * @lh;
}

// Content - everything which is content goes in here
.content {
  background-color: @gallery;

  &.content-inverted {
    background-color: @white;
  }
}

@gallery: #eeeeee;    // light-grey
@silver: #cdcdcd;     // grey
@manatee: #8e8e93;    // dark-grey
@dove-gray: #717171;  // dark-dark-grey
@mid-gray: #616165;   // dark-dark-dark-grey
@ship-gray: #434346;  // dark-dark-dark-dark-grey

.nav-stacked > li {

  background-color: @white;

  + li { margin-top: 0px; }

  &.active a {
    border-color: @tangerine;
    &:hover { border-color: @tangerine; }
  }

  a {
    border-left: @gap-h/2 solid @white;
    &:hover { border-color: @silver; }
  }
}

.navbar-default {
  .navbar-nav {

    margin-left: -15px; // removing left gap
    > li > a {

      &:hover, &:focus {
        border-bottom: 3px solid @silver;
      }
    }

    > .active > a {
      &, &:hover, &:focus {
        border-bottom: 3px solid @tangerine;
      }
    }
  }
}

.list-group a .fa {
  color: @dove-gray;
  &:hover { color: @black; }
}

a.list-group-item,
button.list-group-item {
  &:hover,
  &:focus {
    color: @black;
  }
}

.dropdown-menu {
  white-space: nowrap;

  .dropdown-item {
    i {
      vertical-align: top;
      line-height: @lh;
    }

    span {
      display: inline-block;
      padding-right: @lh + @gap-h;
    }

    small {
      display: block;
      font-weight: 300;
      color: @mid-gray;
    }

    .dropdown-item-color {
      width: @lh;
      height: @lh;
      border: 1px solid @white;
      display: inline-block;
      position: absolute;
      right: 20px;
    }
  }
}

.navbar-form {
  padding-right: 0px; // removing right gap
}

